angular.module("components.auth").factory("components.auth.service",["$http","components.session.service",function($http,sessionService){var authService={};return authService.getUser=function(){return sessionService.getUser()},authService.authorizeCheck=function(next,$location){next.authorization&&(authService.isAuthorized()||$location.url("/"))},authService.isAuthorized=function(){return null!==sessionService.getUser().sessionId},authService.logout=function(){sessionService.remove()},authService.login=function(credentials){return $http.get("/",credentials).then(function(){sessionService.create(credentials)},function(){})},authService}]);